mapper.select:
	select 
  
  from user t
  
  	
  		t.accountname = #{accountname , jdbcType = VARCHAR}
  	
  	
  		and t.foreighid = #{foreighid , jdbcType = VARCHAR}
  	
  	
  		and t.foreightype = #{foreightype , jdbcType = VARCHAR}
  	
  	
  		and t.NickName = #{nickname , jdbcType = VARCHAR}
  	
  
  
  order by t.CreateDate desc
