mapper.select:
select
t2.CommodityId as commodityid, t2.CommodityCatagoryId as commoditycatagoryid, t2.CommodityName as commodityname, t2.CommodityDes as commoditydes, t1.Number as number
from UserPackage t left join Package t1 join Commodity t2 on t1.CommodityId = t2.CommodityId on t.PackageId = t1.PackageId
t.UserId = #{userid,jdbcType=BIGINT}
and t2.CommodityCatagoryId = #{catagoryid,jdbcType=BIGINT}
and t2.CommodityName like concat('%',#{commodityname,jdbcType=VARCHAR},'%')

select t1.Number from UserPackage t left join Package t1 on t.PackageId = t1.PackageId
t.UserId = #{userid,jdbcType=BIGINT}
and t1.CommodityId = #{commodityid,jdbcType=BIGINT}

select
t2.PackageId as packageid, t2.CommodityId as commodityid, t2.Number as number
from
UserPackage t1 join Package t2 on t1.PackageId = t2.PackageId
t1.UserId = #{userid,jdbcType=BIGINT}
and t2.CommodityId = #{commodityid,jdbcType=BIGINT}
